import React, { useState, useEffect, useContext } from 'react';
import Whiteboard from './components/whiteboard';
import UserPanel from './components/UserPanel';
import RoomJoinModal from './components/RoomJoinModal';
import { SocketContext } from './context/SocketContext';
import { EVENTS } from './utils/constants';

function App() {
  const socket = useContext(SocketContext);
  const [users, setUsers] = useState([]);
  const [roomId, setRoomId] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [isJoined, setIsJoined] = useState(false);

  const handleJoinRoom = (userName, room) => {
    const user = {
      id: socket.id,
      name: userName
    };
    setCurrentUser(user);
    setRoomId(room);
    setIsJoined(true);
    
    // Join room
    socket.emit(EVENTS.JOIN_ROOM, { roomId: room, user });
  };

  useEffect(() => {
    if (!socket || !isJoined) return;

    // Listen for user events
    const handleUserJoined = (data) => {
      setUsers(prev => {
        if (!prev.find(u => u.id === data.user.id)) {
          return [...prev, data.user];
        }
        return prev;
      });
    };

    const handleUserLeft = (data) => {
      setUsers(prev => prev.filter(u => u.id !== data.userId));
    };

    const handleRoomUsers = (data) => {
      setUsers(data.users || []);
    };

    socket.on(EVENTS.USER_JOINED, handleUserJoined);
    socket.on(EVENTS.USER_LEFT, handleUserLeft);
    socket.on('room-users', handleRoomUsers);

    // Add current user to the list
    setUsers(prev => {
      if (!prev.find(u => u.id === currentUser.id)) {
        return [...prev, currentUser];
      }
      return prev;
    });

    return () => {
      socket.off(EVENTS.USER_JOINED, handleUserJoined);
      socket.off(EVENTS.USER_LEFT, handleUserLeft);
      socket.off('room-users', handleRoomUsers);
    };
  }, [socket, isJoined, currentUser]);

  // Show modal if not joined
  if (!isJoined) {
    return <RoomJoinModal onJoin={handleJoinRoom} isVisible={true} />;
  }

  return (
    <div className="app">
      <div className="main-content">
        <div className="header">
          <h1>AI Meeting Board</h1>
          <div className="room-info">
            <div className="room-id">Meeting: {roomId}</div>
          </div>
        </div>
        
        <Whiteboard roomId={roomId} users={users} />
      </div>
      
      <UserPanel users={users} roomId={roomId} />
    </div>
  );
}

export default App;
