import React, { useRef, useEffect, useContext, useState } from 'react';
import { Stage, Layer, Line, Circle, Rect } from 'react-konva';
import { SHAPES, EVENTS } from '../utils/constants';
import useDrawing from '../hooks/useDrawing';
import { SocketContext } from '../context/SocketContext';
import { recognizeShape } from '../utils/shapeRecognition';

const Whiteboard = ({ roomId, users }) => {
  const socket = useContext(SocketContext);
  const stageRef = useRef(null);
  const [debugInfo, setDebugInfo] = useState("");
  const [stageSize, setStageSize] = useState({ width: 800, height: 600 });
  const {
    elements,
    setElements,
    isDrawing,
    handleDrawStart,
    handleDrawMove,
    handleDrawEnd
  } = useDrawing(socket, roomId);

  // Update stage size on mount and window resize
  useEffect(() => {
    const updateStageSize = () => {
      const container = stageRef.current?.container();
      if (container) {
        const rect = container.getBoundingClientRect();
        setStageSize({
          width: rect.width,
          height: rect.height
        });
      }
    };

    updateStageSize();
    window.addEventListener('resize', updateStageSize);
    return () => window.removeEventListener('resize', updateStageSize);
  }, []);

  // Socket listeners
  useEffect(() => {
    const handleRemoteDrawStart = (element) => {
      setElements(prev => [...prev, element]);
    };

    const handleRemoteDrawMove = (point) => {
      setElements(prev => {
        if (prev.length === 0) return prev;
        const lastIndex = prev.length - 1;
        const updated = [...prev];
        updated[lastIndex] = {
          ...updated[lastIndex],
          points: [...updated[lastIndex].points, point]
        };
        return updated;
      });
    };

    const handleShapeRecognized = (shape) => {
      setElements(prev => [
        ...prev.filter(el => !el.temp),
        { ...shape, id: Date.now() }
      ]);
      setDebugInfo(`Shape recognized: ${shape.type}`);
    };

    socket.on(EVENTS.DRAW_START, handleRemoteDrawStart);
    socket.on(EVENTS.DRAW_MOVE, handleRemoteDrawMove);
    socket.on(EVENTS.SHAPE_RECOGNIZED, handleShapeRecognized);

    return () => {
      socket.off(EVENTS.DRAW_START, handleRemoteDrawStart);
      socket.off(EVENTS.DRAW_MOVE, handleRemoteDrawMove);
      socket.off(EVENTS.SHAPE_RECOGNIZED, handleShapeRecognized);
    };
  }, [setElements, socket]);

  // Drawing events
  const handleMouseDown = (e) => {
    const stage = e.target.getStage();
    const pos = stage.getPointerPosition();
    handleDrawStart(pos.x, pos.y);
    setDebugInfo("Drawing started...");
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) return;
    const stage = e.target.getStage();
    const pos = stage.getPointerPosition();
    handleDrawMove(pos.x, pos.y);
  };

  const handleMouseUp = () => {
    if (!isDrawing) return;
    handleDrawEnd();
    
    if (elements.length === 0) return;
    
    const lastElement = elements[elements.length - 1];
    if (lastElement.type === SHAPES.FREEHAND && lastElement.points.length >= 8) {
      try {
        console.log("Attempting shape recognition with points:", lastElement.points.length);
        const recognized = recognizeShape(lastElement.points);
        console.log("Recognition result:", recognized);
        
        if (recognized) {
          console.log(`Recognized as ${recognized.type}`);
          socket.emit(EVENTS.SHAPE_RECOGNIZED, {
            roomId,
            shape: {
              ...recognized,
              id: Date.now()
            }
          });
          
          // Remove the temporary freehand drawing
          setElements(prev => prev.filter(el => !el.temp));
        } else {
          console.log("No shape recognized - keeping as freehand");
          setDebugInfo("No shape recognized");
        }
      } catch (error) {
        console.error("Recognition error:", error);
        setDebugInfo(`Recognition error: ${error.message}`);
      }
    }
  };

  // Add temporary debug layer
  const renderDebugLayer = () => {
    if (!isDrawing || elements.length === 0) return null;
    
    const lastElement = elements[elements.length - 1];
    if (lastElement.type !== SHAPES.FREEHAND) return null;
    
    return (
      <Layer>
        {lastElement.points.map((point, i) => (
          <Circle
            key={`point-${i}`}
            x={point[0]}
            y={point[1]}
            radius={2}
            fill="red"
            opacity={0.5}
          />
        ))}
        
        {/* Bounding box */}
        {lastElement.points.length > 10 && (
          <Rect
            x={Math.min(...lastElement.points.map(p => p[0]))}
            y={Math.min(...lastElement.points.map(p => p[1]))}
            width={
              Math.max(...lastElement.points.map(p => p[0])) - 
              Math.min(...lastElement.points.map(p => p[0]))
            }
            height={
              Math.max(...lastElement.points.map(p => p[1])) - 
              Math.min(...lastElement.points.map(p => p[1]))
            }
            stroke="blue"
            strokeWidth={1}
            dash={[5, 5]}
          />
        )}
      </Layer>
    );
  };

  return (
    <div className="whiteboard">
      <div className="debug-info">{debugInfo}</div>
      
      <Stage
        width={stageSize.width}
        height={stageSize.height}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        ref={stageRef}
      >
        <Layer>
          {elements.map((element) => {
            if (element.type === SHAPES.FREEHAND) {
              return (
                <Line
                  key={element.id}
                  points={element.points.flat()}
                  stroke="#000"
                  strokeWidth={3}
                  tension={0.5}
                  lineCap="round"
                  lineJoin="round"
                />
              );
            }
            if (element.type === SHAPES.CIRCLE) {
              return (
                <Circle
                  key={element.id}
                  x={element.x + element.radius}
                  y={element.y + element.radius}
                  radius={element.radius}
                  stroke="#000"
                  strokeWidth={3}
                />
              );
            }
            if (element.type === SHAPES.RECTANGLE) {
              return (
                <Rect
                  key={element.id}
                  x={element.x}
                  y={element.y}
                  width={element.width}
                  height={element.height}
                  stroke="#000"
                  strokeWidth={3}
                />
              );
            }
            return null;
          })}
        </Layer>
        
        {renderDebugLayer()}
      </Stage>
    </div>
  );
};

export default Whiteboard;
