export function recognizeShape(points) {
  if (!points || points.length < 8) return null;

  // Calculate bounding box
  const xs = points.map(p => p[0]);
  const ys = points.map(p => p[1]);
  const minX = Math.min(...xs);
  const maxX = Math.max(...xs);
  const minY = Math.min(...ys);
  const maxY = Math.max(...ys);
  const width = maxX - minX;
  const height = maxY - minY;
  const centerX = (minX + maxX) / 2;
  const centerY = (minY + maxY) / 2;

  // Calculate path length
  let pathLength = 0;
  for (let i = 1; i < points.length; i++) {
    pathLength += Math.hypot(
      points[i][0] - points[i-1][0],
      points[i][1] - points[i-1][1]
    );
  }

  // Calculate shape closure (very lenient for hand-drawn shapes)
  const startEndDist = Math.hypot(
    points[0][0] - points[points.length-1][0],
    points[0][1] - points[points.length-1][1]
  );
  const isClosedShape = startEndDist < Math.min(width, height) * 0.5; // Very lenient closure check

  // Calculate scores
  const circleScore = calculateCircleScore(points, centerX, centerY, width, height, pathLength);
  const rectScore = calculateRectangleScore(points, width, height, pathLength);

  console.log('Shape Analysis:', {
    points: points.length,
    width,
    height,
    startEndDist,
    isClosedShape,
    circleScore,
    rectScore
  });

  // Even more lenient thresholds for hand-drawn shapes
  if (circleScore > 0.5 && circleScore > rectScore) {
    const radius = Math.min(width, height) / 2;
    return {
      type: 'circle',
      x: centerX - radius,
      y: centerY - radius,
      radius: radius
    };
  }
  
  if (rectScore > 0.5 && rectScore > circleScore) {
    return {
      type: 'rectangle',
      x: minX,
      y: minY,
      width: width,
      height: height
    };
  }

  return null;
}

function calculateCircleScore(points, centerX, centerY, width, height, pathLength) {
  // Calculate average distance from center
  let totalDist = 0;
  points.forEach(point => {
    totalDist += Math.hypot(point[0] - centerX, point[1] - centerY);
  });
  const avgDist = totalDist / points.length;

  // Calculate variance (lower is better for circles)
  let variance = 0;
  points.forEach(point => {
    variance += Math.pow(Math.hypot(point[0] - centerX, point[1] - centerY) - avgDist, 2);
  });
  variance = variance / points.length;

  // Expected circumference
  const radius = Math.min(width, height) / 2;
  const expectedCirc = 2 * Math.PI * radius;
  
  // Aspect ratio (1 = perfect circle)
  const aspectRatio = Math.min(width, height) / Math.max(width, height);

  // Combine factors with adjusted weights for hand-drawn shapes
  const varianceScore = 1 - Math.min(variance / (radius * radius * 2), 1); // More lenient variance
  const circScore = 1 - Math.abs(pathLength - expectedCirc) / expectedCirc;
  const aspectScore = aspectRatio;

  const finalScore = (varianceScore * 0.3 + circScore * 0.4 + aspectScore * 0.3);
  
  console.log('Circle Score Components:', {
    varianceScore,
    circScore,
    aspectScore,
    finalScore
  });

  return finalScore;
}

function calculateRectangleScore(points, width, height, pathLength) {
  // Count direction changes (should be around 4 for rectangle)
  let dirChanges = 0;
  const directions = [];
  
  for (let i = 1; i < points.length; i++) {
    const dx = points[i][0] - points[i-1][0];
    const dy = points[i][1] - points[i-1][1];
    if (Math.abs(dx) > 0.5 || Math.abs(dy) > 0.5) { // Even more lenient movement threshold
      directions.push(Math.atan2(dy, dx));
    }
  }

  for (let i = 1; i < directions.length; i++) {
    const angleDiff = Math.abs(directions[i] - directions[i-1]);
    if (angleDiff > Math.PI/8) dirChanges++; // More lenient angle change detection
  }

  // Expected perimeter
  const expectedPerim = 2 * (width + height);
  const perimScore = 1 - Math.abs(pathLength - expectedPerim) / expectedPerim;

  // Angle change score (should be around 4 changes, but very lenient)
  const changeScore = 1 - Math.abs(dirChanges - 4) / 8;

  // Aspect ratio score (penalize very thin rectangles)
  const minSide = Math.min(width, height);
  const maxSide = Math.max(width, height);
  const aspectScore = minSide / maxSide;

  const finalScore = (perimScore * 0.25 + changeScore * 0.55 + aspectScore * 0.2);
  
  console.log('Rectangle Score Components:', {
    dirChanges,
    perimScore,
    changeScore,
    aspectScore,
    finalScore
  });

  return finalScore;
}
